import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonCheckbox, IonBadge, IonFab, IonFabButton, IonIcon, useIonViewWillEnter } from '@ionic/react';
import React, { useState } from 'react';
import { add } from 'ionicons/icons';
import { RouteComponentProps } from 'react-router';

interface Task {
  name: String;
  urgence: Number;
}



const Home: React.FC<RouteComponentProps> = (props) => {
  const [tasks, setTask] = useState<Task[]>([]);

  useIonViewWillEnter(async () => {
    const result = await fetch('http://localhost:3000/tasks', {
    });
    const data = await result.json();
    setTask(data);
    console.log(data);
  });

  return (
    <IonPage>

      <IonHeader>
        <IonToolbar>
          <IonTitle>Listando suas tarefas</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>

        <IonList >
          {tasks.map((task, idx) => <TaskItem key={idx} task={task} />)}
        </IonList>

      </IonContent>

      <IonFab vertical="bottom" horizontal="end" slot="fixed">
        <IonFabButton onClick={() => props.history.push('/newTask')}>
          <IonIcon icon={add} />
        </IonFabButton>
      </IonFab>

    </IonPage>
  );
};

const TaskItem: React.FC<{ task: Task }> = ({ task }) => {
  return (
    <IonItem>
      <IonCheckbox slot="start" />

      <IonLabel>

      </IonLabel>

      <IonBadge color="warning" slot="end">
        Priority: 3
    </IonBadge>

    </IonItem>
  );
}



export default Home;
